function Get-AbrAzBastion {
    <#
    .SYNOPSIS
        Used by As Built Report to retrieve Azure Bastion information
    .DESCRIPTION

    .NOTES
        Version:        0.1.2
        Author:         Tim Carman
        Twitter:        @tpcarman
        Github:         tpcarman
    .EXAMPLE

    .LINK

    #>
    [CmdletBinding()]
    param (
    )

    begin {
        Write-PScriboMessage "Bastion InfoLevel set at $($InfoLevel.Bastion)."
    }

    process {
        Try {
            if ($InfoLevel.Bastion -gt 0) {
                $AzBastions = Get-AzBastion | Sort-Object Name
                if ($AzBastions) {
                    Write-PScriboMessage "Collecting Azure Bastion information."
                    Section -Style Heading4 'Bastion' {
                        if ($Options.ShowSectionInfo) {
                            Paragraph "Azure Bastion is a service you deploy that lets you connect to a virtual machine using your browser and the Azure portal, or via the native SSH or RDP client already installed on your local computer."
                            BlankLine
                            Paragraph "The Azure Bastion service is a fully platform-managed PaaS service that you provision inside your virtual network. It provides secure and seamless RDP/SSH connectivity to your virtual machines directly from the Azure portal over TLS. When you connect via Azure Bastion, your virtual machines don't need a public IP address, agent, or special client software."
                            BlankLine
                            Paragraph "Bastion provides secure RDP and SSH connectivity to all of the VMs in the virtual network in which it is provisioned. Using Azure Bastion protects your virtual machines from exposing RDP/SSH ports to the outside world, while still providing secure access using RDP/SSH."
                            BlankLine
                            Try {
                                Image -Text 'Bastion Architecture' -Align 'Center' -Percent 45 -Base64 ""
                                BlankLine
                            } Catch {
                                Write-PScriboMessage -IsWarning "Unable to display Bastion image."
                            }
                        }
                        $AzBastionInfo = @()
                        foreach ($AzBastion in $AzBastions) {
                            $InObj = [Ordered]@{
                                'Name' = $AzBastion.Name
                                'Resource Group' = $AzBastion.ResourceGroupName
                                'Location' = $AzLocationLookup."$($AzBastion.Location)"
                                'Subscription' = "$($AzSubscriptionLookup.(($AzBastion.Id).split('/')[2]))"
                                'Virtual Network / Subnet' = $AzBastion.IpConfigurations.subnet.id.split('/')[-1]
                                'Public DNS Name' = $AzBastion.DnsName
                                'Public IP Address' = $AzBastion.IpConfigurations.publicipaddress.id.split('/')[-1]
                            }

                            if ($Options.ShowTags) {
                                $InObj['Tags'] = If ([string]::IsNullOrEmpty($AzBastion.Tag)) {
                                    'None'
                                } else {
                                    ($AzBastion.Tag.GetEnumerator() | ForEach-Object { "$($_.Name):`t$($_.Value)" }) -join [Environment]::NewLine
                                }
                            }

                            $AzBastionInfo += [PSCustomObject]$InObj
                        }

                        if ($InfoLevel.Bastion -ge 2) {
                            Paragraph "The following sections detail the configuration of the bastions within the $($AzSubscription.Name) subscription."
                            foreach ($AzBastion in $AzBastionInfo) {
                                Section -Style NOTOCHeading5 -ExcludeFromTOC "$($AzBastion.Name)" {
                                    $TableParams = @{
                                        Name = "Bastion - $($AzBastion.Name)"
                                        List = $true
                                        ColumnWidths = 40, 60
                                    }
                                    if ($Report.ShowTableCaptions) {
                                        $TableParams['Caption'] = "- $($TableParams.Name)"
                                    }
                                    $AzBastion | Table @TableParams
                                }
                            }
                        } else {
                            Paragraph "The following table summarises the configuration of the bastions within the $($AzSubscription.Name) subscription."
                            BlankLine
                            $TableParams = @{
                                Name = "Bastions - $($AzSubscription.Name)"
                                List = $false
                                Columns = 'Name', 'Resource Group', 'Location', 'Public IP Address'
                                ColumnWidths = 25, 25, 25, 25
                            }
                            if ($Report.ShowTableCaptions) {
                                $TableParams['Caption'] = "- $($TableParams.Name)"
                            }
                            $AzBastionInfo | Table @TableParams
                        }
                    }
                }
            }
        } Catch {
            Write-PScriboMessage -IsWarning $($_.Exception.Message)
        }
    }

    end {}
}